mapper.select:
SELECT
IFNULL(SUM( CASE WHEN leave_type=#{leave.paramValue,jdbcType=VARCHAR} THEN `total_count` ELSE 0 END), 0) AS #{leave.paramName,jdbcType=VARCHAR}
FROM manage_leave where user_id = #{userId,jdbcType=BIGINT} and status = 1

select ml.* from manage_leave ml left join sys_user su on ml.user_id = su.user_id


        su.super_id = #{dealId}


        and ml.status = #{status}


order by ml.leave_id desc

    limit #{page,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select count(*) from manage_leave ml left join sys_user su on ml.user_id = su.user_id
where su.super_id = #{dealId}

    and ml.status = #{status}
